create schema JobPortalSystem;
create table Jobs (JobID int(50) primary key,
                   Title varchar(100),
                   Description varchar(200),
                   CompanyID int(50),
                   Location varchar(200),
                   Salary int(50),
                   PostedDate Date,
                   ApplicationDeadline Date);

select * from Jobs;

create table Companies (CompanyId int primary key, 
						Name varchar(200), 
                        Description varchar(200), 
                        website varchar(100), 
                        Location varchar(200), 
                        Industry varchar(100), 
                        ContactEmail varchar(100));
                        

select * from Companies;

create table Applicants (ApplicantID int Primary Key, 
						 FirstName varchar(20), 
                         LastName varchar(20), 
                         Email varchar(20), 
                         Phone int(11), 
                         Resume varchar(100), 
                         LinkedInProfile varchar(100));

                         
create table Applications (ApplicationID int primary key, JobID int, ApplicantID int, ApplicationDate Date, Status varchar(50));



CREATE TABLE Reviews (
    ReviewID INT AUTO_INCREMENT PRIMARY KEY,
    CompanyID INT,
    ReviewerName VARCHAR(100),
    ReviewDate DATE,
    Rating TINYINT,
    Comment TEXT);

